import React from 'react';
import { Container, Typography, Box, Grid, Card, CardContent, CardMedia, CardActions, Button } from '@mui/material';

function Projects() {
  const projects = [
    {
      id: 1,
      title: "Project 1",
      description: "Description of your first project. Explain what technologies you used and what you learned.",
      image: "/project1.jpg",
      demoLink: "#",
      githubLink: "#"
    },
    {
      id: 2,
      title: "Project 2",
      description: "Description of your second project. Explain what technologies you used and what you learned.",
      image: "/project2.jpg",
      demoLink: "#",
      githubLink: "#"
    },
    {
      id: 3,
      title: "Project 3",
      description: "Description of your third project. Explain what technologies you used and what you learned.",
      image: "/project3.jpg",
      demoLink: "#",
      githubLink: "#"
    }
  ];

  return (
    <Container>
      <Box sx={{ mt: 8 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          My Projects
        </Typography>
        <Grid container spacing={4} sx={{ mt: 2 }}>
          {projects.map((project) => (
            <Grid item xs={12} sm={6} md={4} key={project.id}>
              <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                <CardMedia
                  component="img"
                  height="140"
                  image={project.image}
                  alt={project.title}
                  sx={{ backgroundColor: '#f5f5f5' }}
                />
                <CardContent sx={{ flexGrow: 1 }}>
                  <Typography gutterBottom variant="h5" component="div">
                    {project.title}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {project.description}
                  </Typography>
                </CardContent>
                <CardActions>
                  <Button size="small" href={project.demoLink} target="_blank">View Demo</Button>
                  <Button size="small" href={project.githubLink} target="_blank">GitHub</Button>
                </CardActions>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Box>
    </Container>
  );
}

export default Projects;
